import React, { useContext, useState,  useEffect } from 'react'
import { DataContext } from './DataProvider'
import Colors from './Colors'
import Sizes from './Sizes'
import { Link } from 'react-router-dom'




export default function Cart() {

    const value = useContext(DataContext)
    const [cart, setCart] = value.cart;
    const [total, setTotal] = useState(0)

    useEffect(() => {
        const getTotal = () => {
            const res = cart.reduce((prev, item) => {
                return prev + (item.price * item.count)


            }, 0)
            setTotal(res)
        }
        getTotal()
    }, [cart])


    const reduction = id => {
        cart.forEach(item => {
            if (item._id === id) {
                item.count === 1 ? item.count = 1 : item.count -= 1;

            }
        })
        setCart([...cart])
    }


    const increase = id => {
        cart.forEach(item => {
            if (item._id === id) {
                item.count += 1;

            }
        })
        setCart([...cart])
    }

const removeProduct=id=>{
    if(window.confirm("Do you want to delete this product")){
        cart.forEach((item,index)=>{
            if(item._id===id){
                cart.splice(index,1)

                            }
            
        })

        setCart([...cart])
    }
}

    
    if (cart.length === 0)

    return (
        <>
        <div>
        <br /><h2 style={{ textAlign: "center"}}>   Cart Empty</h2>
            {
                cart.map(product => (
                    <div className="details cart" key={product._id}>
                        <div className="img-container"
                            style={{ backgroundImage: `url(${product.image[0]})` }} />

                        <div className="box-details">
                            <h2 title={product.title}>{product.title}</h2>

                            <h3>${product.price}</h3>
                            <Sizes sizes={product.sizes} />


                            <p>{product.description}</p>
                            <p>{product.content}</p>

                            {/* <DetailsThumb image={product.image} setIndex={setIndex} /> */}
                            <div className="amount">
                                <button className="count" onClick={() => reduction(product._id)}>-</button>
                                <span>{product.count}</span>
                                <button className="count" onClick={() => increase(product._id)}> +</button>

                            </div>

                            <div className="delete" onClick={()=> removeProduct(product._id)}> 
                                X
                            </div>





                        </div>



                    </div>

                ))
            }

            <div className="total">
                <Link to="/payment">Payment</Link>
                <h3>Total:${total}</h3>

            </div>
            </div>
        </>
    )
}
